import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      doguinhos: undefined,
    }
  }

  async fetchDoguinhos() {
    const requestDogs = await fetch("https://dog.ceo/api/breeds/image/random");
    const requestObject = await requestDogs.json();
    this.setState({
      doguinhos: requestObject
    })
  }

  componentDidMount() {
    this.fetchDoguinhos()
  }

  render() {
    const { message } = this.state
    return (
      <section>
        <h1>Loading...</h1>
        <img src={message} />
        <button>Próximo</button>
      </section>

    );
  }
}

export default App;
