import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      doguinhos: "",
    };
  }

  componentDidMount() {
    this.fetchDoguinhos();
  }

  async fetchDoguinhos() {
    const requestDogs = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObject = await requestDogs.json();
    this.setState({
      doguinhos: requestObject.message,
    });
  }

  render() {
    const { doguinhos, message } = this.state;
    return (
      <section>
        <h1>Doguinhos e afins</h1>
        <img src={ doguinhos } alt="dogs" />
        <br />
        <button
          type="button"
          onClick={ () => this.setState((i) => ({ doguinhos: i.doguinhos })) }
        >
          Próximo
        </button>
      </section>

    );
  }
}

export default App;
