import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      doguinhos: undefined,
      arrayDogs: []
    };
  }

  componentDidMount() {
    this.fetchDoguinhos();
  }

  updateDogs = () => {
    this.setState(({arrayDogs, doguinhos}) => ({
      arrayDogs: [...arrayDogs, doguinhos]
    }))
  }

  async fetchDoguinhos() {
    const requestDogs = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObject = await requestDogs.json();
    this.setState({
      doguinhos: requestObject.message,
    });
  }

  render() {
    const { doguinhos } = this.state;
    return (
      <section>
        <h1>Doguinhos e afins</h1>
        <img src={ doguinhos } alt="dogs" />
        <br />
        <button type="button" onClick={ this.updateDogs }>Próximo</button>
      </section>

    );
  }
}

export default App;
